var client = require('./logclient.js');

function baelogAppender (config) {
	var LogClient = new client.LogClient(config);
	return function(loggingEvent) {
		LogClient.log(loggingEvent);
	};
}

function configure(config) {
  return consoleAppender(config);
}

exports.appender = baelogAppender;
exports.configure = configure;
